mapper.select:
SELECT s.* FROM server s
LEFT JOIN server_monitoring_rule sm ON s.id = sm.server_id WHERE sm.monitoring_rule_id = #{ruleId}
WHERE 1 = 1

mapper.update:
UPDATE server
SET server_name = #{serverName}, hostname = #{hostname}, description = #{description},
created_by = #{createdBy}, updated_by = #{updatedBy}, create_date = #{createDate}, update_date = #{updateDate}
WHERE id = #{id}
